import React, { useState } from 'react'
import { FileUploader } from "react-drag-drop-files";
import ReactCompareImage from 'react-compare-image';
import Lottie from 'react-lottie';
import * as loadingAnimation from './loading.json'
import './common.css';

const fileTypes = ["JPG", "JPEG", "PNG"];
const cartoonify_api = 'http://localhost:5000/cartoonify'

const lottieOptions = {
	loop: true,
	autoplay: true, 
	animationData: loadingAnimation,
	rendererSettings: {
	  preserveAspectRatio: 'xMidYMid slice'
	}
};

const Cartoonify = () => {
	const [isLoading, setIsLoading] = useState(false);
	const [file, setFile] = useState(null);
	const [cartoonFile, setCartoonFile] = useState(null);
	
  	const handleChange = (file) => {
		setCartoonFile(null); //Reset cartoon file
    	setFile(file);
  	}

	function sendCartoonifyRequest() {
		setIsLoading(true);
		const formData = new FormData();
    	formData.append('image', file);

		const requestOptions = {
			method: 'POST',
			body: formData
		  };
	  
		  fetch(cartoonify_api, requestOptions)
		  .then(response => response.blob())
		  .then(imageBlob => {
			const cartoonImageObjectURL = URL.createObjectURL(imageBlob);
      		console.log(cartoonImageObjectURL);
			setCartoonFile(cartoonImageObjectURL)
		  })
		  .catch(err => {
			setCartoonFile(null)
			console.log(err)
		  }).finally(() => {
			setIsLoading(false)
		  });
	}

	function renderDragDropUi() {
		return (
		<div className='drag-drop-container'>
			<p className='drag-drop-text p-3'>
				<strong>Choose a file</strong> or drag it here
			</p>
			<p className='text-center font-weight-bold'>{file != null ? `File ${file.name} received, press 'Cartoonify'!` : 'Awaiting your upload...'}</p>
		</div>
		)
	}

	return (
		<div className='container mt-5'>
			<div className='row'>
				<FileUploader 
							children={renderDragDropUi()}
							name="file" 
							types={fileTypes}
							hoverTitle="Drop here"
							handleChange={handleChange} 
							onTypeError={(err) => { window.alert('File type not supported') }}
							onSizeError={(err) => { window.alert('File size is too big') }} />
				{file != null ?
				<button className='btn btn-md drag-drop-btn mt-3 rounded-0 w-100' onClick={() => sendCartoonifyRequest()} disabled={file == null}>Cartoonify!</button>
				: null}
			</div>

			{isLoading ? 
			<Lottie options={lottieOptions}
			height={400}
			width={400} />
			:
			<div id="" style={{ maxWidth: '640px' }}>
				<ReactCompareImage leftImage={file != null ? URL.createObjectURL(file) : null}
								rightImage={cartoonFile} />
				{file != null & cartoonFile != null ?
				<div className='row text-center'>
					<a className="btn btn-sm download-btn col-12 my-2 py-1" href={URL.createObjectURL(file)} download>Download Original Image</a>
					<a className="btn btn-sm download-btn col-12" href={cartoonFile} download>Download Cartoon Image</a>
				</div>
				: null}
			</div>
			}
		</div>
	);
};

export default Cartoonify;
