import React, { useState } from 'react'
import { FileUploader } from "react-drag-drop-files";
import './common.css'

const fileTypes = ["JPG", "PNG"];
const cartoonify_api = 'http://localhost:5000/cartoonify'

const Cartoonify = () => {
	const [file, setFile] = useState(null);
	const [cartoonFile, setCartoonFile] = useState(null);
	
  	const handleChange = (file) => {
		const imgObjUrl = URL.createObjectURL(file);
		console.log(imgObjUrl)
    	setFile(file);
  	}

	function sendCartoonifyRequest() {
		const formData = new FormData();
    	formData.append('image', file);

		const requestOptions = {
			method: 'POST',
			body: formData
		  };
	  
		  fetch(cartoonify_api, requestOptions)
		  .then(response => response.json())
		  .then(result => {
			console.log(result);
		  })
		  .catch(err => {
			console.log(err)
		  });
	}

	return (
		<div className=''>
		<FileUploader 
					  name="file" 
					  types={fileTypes}
					  hoverTitle="Drop here"
					  handleChange={handleChange} 
					  onTypeError={(err) => { window.alert(err) }}
					  onSizeError={(err) => { window.alert(err) }} />
		{ file != null ? 
		<>
		<img width={500} src={URL.createObjectURL(file)} />
		<button className='btn btn-sm btn-success' onClick={() => sendCartoonifyRequest()}>Cartoonify!</button>
		</>
		 : null}
		</div>
	);
};

export default Cartoonify;
